import React from 'react';

const StarRating = ({rating}) => {
    const stars = [];
    for (let l=1; l<=5;l++){
        if(l<=rating){
            stars.push(<i className='fas fa-star'></i>);
        }
        else if(l===Math.ceil(rating) ){
            stars.push()
        }
        else{
            stars.push(<i className="far fa-star"></i>);
        }
    }
    return (
        <div>
            {stars}
        </div>
    )
}

export default StarRating;
