import React from 'react'

const StarRating = ({rating}) => {
    const stars = [];
    for (let l=1; l<=5;l++){
        if(i<=rating){
            stars.push(<i class='fas fa-start'></i>);
        }
        else{
            stars.push(<i class='far fa-start'></i>);
        }
    }
    return (
        <div>
            {stars}
        </div>
    )
}

export default StarRating;
